import { Component, Input } from "@angular/core";

@Component({
  selector: "app-user",
  template: `
    <input type="text" [(ngModel)]="name" />
    <p>Hello {{ name }}!</p>
    <p>I'm the user component</p>
  `,
})
export class UserComponent {
  @Input() name = "Alejandro";

  onUserInput(event: any) {
    this.name = event.target.value;
  }
}
