import { Component } from "@angular/core";
import { characterType } from "../../types/character";
import { StarWarsService } from "../star-wars.service";

@Component({
  selector: "app-tabs",
  templateUrl: "./tabs.component.html",
  styleUrls: ["./tabs.component.css"],
})
export class TabsComponent {
  characters: characterType[] = [];

  chosenList: string = "all";

  onChoose(side: string) {
    this.chosenList = side;
  }

  getCharacters() {}

  onSideChosen(charInfo: any) {
    const position = this.characters.findIndex((char) => {
      return char.name === charInfo.name;
    });
    this.characters[position].side = charInfo.side;
  }
}
<div>
  <ul class="nav nav-tabs">
    <li class="nav-item">
      <a
        class="nav-link"
        (click)="onChoose('all')"
        style="cursor: pointer"
        [ngClass]="{ active: chosenList === 'all' }"
        >All</a
      >
    </li>

    <li class="nav-item">
      <a
        class="nav-link"
        (click)="onChoose('light')"
        style="cursor: pointer"
        [ngClass]="{ active: chosenList === 'light' }"
        >Light</a
      >
    </li>

    <li class="nav-item">
      <a
        class="nav-link"
        (click)="onChoose('dark')"
        style="cursor: pointer"
        [ngClass]="{ active: chosenList === 'dark' }"
        >Dark</a
      >
    </li>
  </ul>
  <app-list
    [characters]="getCharacters()"
    (sideAssigned)="onSideChosen($event)"
  ></app-list>
</div>
